import React from 'react'
import Back from "../assets/aero.png"

const Login = () => {
  var sectionStyle = {
    backgroundImage: `url(${Back})`,
    backgroundRepeat: "no-repeat",
    backgroundSize: "cover",
    opacity: 0.6

  };
  // return (
  //   <div className='d-flex flex-column min-vh-100 text-center bg-image' style={sectionStyle}>
  //     <main>
  //       <form  action="">

  //         <div className="form-floating">
  //           <input />
  //         </div>
  //       </form>
  //     </main>
  //   </div>
  //   )
  return (
    <div className='d-flex flex-column min-vh-100 text-center bg-image' style={sectionStyle}>
      <main className='form-signin' >
      <form method="POST" action="">
          <h3 className="card-title mb-3">Sign in</h3>
          <div className="form-floating">
            <input type="email" className="form-control" id="floatingInput" name="username" placeholder="name@example.com" required></input>
              <label for="floatingInput">Email address</label>
              <div className="form-floating">
                <input type="password" className="form-control" id="floatingPassword" name="password" placeholder="Password" required></input>
                <label for="floatingPassword">Password</label>
                <br></br>
                <div className="text-white">
                <a href="#" className="mb-auto text-dark"><h6>Dont have an account? Sign up</h6></a>
                <br></br>
                <input c type="submit" value="Login" />
                </div>                
              </div>
          </div>
        </form>
      </main>
      </div>
  )
}

export default Login
