const SearchFlights = () => {

    function roundtrip()  {
        const event = document.querySelector("#round")
        console.log(event)
        const result = document.querySelector('#return-date');
        // if(event.checked){
        //     result.innerHTML='';
        // }
        // else{
        //     result.innerHTML="";
        // }
    };
    return ( 
        <>
        <div className="d-flex flex-column p-3">
            <div className="d-flex flex-column">
                <ul className="d-flex ">
                    <li className="d-flex align-items-center flex-fill">
                        <input type="radio" name="trip-type" id="one-way" defaultChecked onClick={roundtrip()} />
                        <p className="my-0 ms-3">One-way</p>
                    </li>
                    <li id="round-trip" className="d-flex align-items-center flex-fill">
                        <input type="radio" name="trip-type" id="round" onClick={roundtrip()} />
                        <p className="my-0 ms-3">Round-trip</p>
                    </li>
                </ul>
            </div>
            <div className="d-flex">
                <div className="flex-fill">
                    <p>From</p>
                    <input type="text" className="form-control" />
                </div>
                <button className="btn btn-primary btn-sm m-1">rev</button>
                <div className="flex-fill">
                    <p>To</p>
                    <input type="text" className="form-control" />
                </div>
            </div>
            <div className="d-flex">
                <div className="flex-fill">
                    <p>Departure</p>
                    <input type="date" className="form-control" />
                </div>
                <div id="return-date" className="flex-fill">
                    <p>Return</p>
                    <input type="date" disabled className="form-control" />
                </div>
            </div>
            <div className="container">
                <div className="column">
                    <p>Number of Passengers</p>
                    <input type="number" className="form-control" />
                    
                </div>
                <div className="column">
                    <p>Class</p>
                    <select className="form-select" name="class">
                        <option value="economy">Economy</option>
                        <option value="business">Business</option>
                    </select>
                </div>
            </div>
            <button className="btn btn-lg flex-fill btn-primary mt-2">Search Flights</button>
        </div>
        </>
     );
}

// functio
 
export default SearchFlights;
