import {useState} from "react"


const Searchtabs = () => {

  function clickedLink(ind){
    if(!tabs[ind].active){
      tabs[ind].active = true
      tabs[!ind].acive = false
      setTab(tabs)
    }
  }

    const [tabs, setTab] = useState([
        {id:1,
        name:"Flights",
        active: false},
        {id:2,
        name:"Hotels",
        active: true}
    ]);


    return ( 
    <>
    <ul className="nav nav-pills nav-justified">
  <li className="nav-item">
    <a onClick={() => clickedLink(0)} className={"nav-link " + (tabs[0].active && "active")} >Flights</a>
  </li>
  <li className="nav-item">
    <a onClick={() => clickedLink(1)} className={"nav-link " + (tabs[1].active && "active")} >Hotels</a>
  </li>
</ul>
    </> );
}


 
export default Searchtabs;
