import SignUpScreen from './screens/SignUpScreen';
import DisplayScreen from './screens/DisplayScreen';
import BookingScreen from './screens/BookingScreen'

function App() {
  const tabs = [
  {
    id:1,
    name: "Home"
  },

  ]


