const FareDetails = () => {
    const passNum = 3
    const fare = 8000
    const basefare = fare*passNum
    const tax = round(basefare*0.05)
    return ( 
        <>
            <div className="container border">
                <h2 className="row">Fare Details</h2>
                <p className="row">Base fare = {passNum}x₹{fare} = ₹{basefare}</p>
                <p className="row">Taxes and Surcharges = ₹{tax}</p>
                <label htmlFor="" className="row">
                    <input type="checkbox" name="Tnc" id="tnc" />
                    Accept the <a>Cancelation policy</a> and <a > Terms and Conditions of use</a>
                </label>
                <button className="btn btn-primary">Book Now</button>
            </div>
        </>
     );
}
 
export default FareDetails;
